let blockIndex = 0;
let charIndex = 0;
const speed = 50;           // velocidad de escritura
const eraseSpeed = 30;      // velocidad de borrado
const delayAfterBlock = 3000; // pausa después de escribir un bloque

function typeBlock() {
  if (charIndex < blocks[blockIndex].length) {
    terminal.textContent += blocks[blockIndex][charIndex];
    charIndex++;
    setTimeout(typeBlock, speed);
  } else {
    setTimeout(eraseBlock, delayAfterBlock);
  }
}

function eraseBlock() {
  if (charIndex >= 0) {  
    terminal.textContent = blocks[blockIndex].substring(0, charIndex);
    charIndex--;
    setTimeout(eraseBlock, eraseSpeed);
  } else {
    // pasar al siguiente bloque
    blockIndex = (blockIndex + 1) % blocks.length;
    charIndex = 0;
    setTimeout(typeBlock, speed);
  }
}

typeBlock();

