function openProjectModal(projectId) {
    const modal = document.getElementById('projectModal');

    if (projectData[projectId]) {
        document.getElementById('modalTitle').innerHTML = projectData[projectId].title;
        document.getElementById('modalMeta').innerHTML = projectData[projectId].meta;
        document.getElementById('modalBody').innerHTML = projectData[projectId].body;
        document.getElementById('modalLinks').innerHTML = projectData[projectId].links;
    }

    modal.classList.add("show");
    document.documentElement.style.overflow = "hidden"; // 🔥 bloquea scroll de html
    document.body.style.overflow = "hidden"; // 🔥 bloquea scroll de body
}

function closeModal() {
    const modal = document.getElementById('projectModal');
    modal.classList.remove("show");
    document.documentElement.style.overflow = ""; // 🔓 restaura
    document.body.style.overflow = "";
}

window.onclick = function(event) {
    const modal = document.getElementById('projectModal');
    if (event.target === modal) {
        closeModal();
    }
};

// Navegación activa al hacer scroll
document.addEventListener('DOMContentLoaded', function() {
    const sections = document.querySelectorAll('section[id]');
    const navLinks = document.querySelectorAll('.nav-links a');
    function updateActiveNav() {
        const scrollPos = window.scrollY + 100;
        sections.forEach(section => {
            const sectionTop = section.offsetTop;
            const sectionHeight = section.offsetHeight;
            const sectionId = section.getAttribute('id');
            if (scrollPos >= sectionTop && scrollPos < sectionTop + sectionHeight) {
                navLinks.forEach(link => {
                    link.classList.remove('active');
                    if (link.getAttribute('href') === `#${sectionId}`) {
                        link.classList.add('active');
                    }
                });
            }
        });
    }
    window.addEventListener('scroll', updateActiveNav);
    updateActiveNav();
});

