// Background stars
const backgroundStars = Array.from({length: 30}, () => ({
    x: Math.random() * width,
    y: Math.random() * height,
    r: Math.random() * 2 + 1,
    color: "#fff"
}));

// SVG filter for glow effect
svg.innerHTML = `
<defs>
    <filter id="glow" x="-50%" y="-50%" width="200%" height="200%">
        <feGaussianBlur stdDeviation="6" result="coloredBlur"/>
        <feMerge>
            <feMergeNode in="coloredBlur"/>
            <feMergeNode in="SourceGraphic"/>
        </feMerge>
    </filter>
</defs>
`;

function getSVGCoords(e) {
    const rect = svg.getBoundingClientRect();
    return {
        x: ((e.clientX - rect.left) / rect.width) * width,
        y: ((e.clientY - rect.top) / rect.height) * height
    };
}

function drawGalaxy() {
    [...svg.querySelectorAll('g, text, circle')].forEach(el => {
        if (!el.closest('defs')) el.remove();
    });

    // Fondo estrellado animado
    backgroundStars.forEach((star, idx) => {
        let s = document.createElementNS("http://www.w3.org/2000/svg", "circle");
        s.setAttribute("cx", star.x);
        s.setAttribute("cy", star.y);
        s.setAttribute("r", star.r);
        s.setAttribute("fill", star.color);
        let blink = 0.7 + 0.3 * Math.sin(frameCount / 15 + idx);
        s.setAttribute("opacity", blink);
        star.x += Math.sin(rotation + idx) * 0.1;
        star.y += Math.cos(rotation + idx) * 0.1;
        if (star.x < 0) star.x = width;
        if (star.x > width) star.x = 0;
        if (star.y < 0) star.y = height;
        if (star.y > height) star.y = 0;
        svg.appendChild(s);
    });

    // Planeta central
    let planetGroup = document.createElementNS("http://www.w3.org/2000/svg", "g");
    planetGroup.setAttribute("transform", `translate(${center.x},${center.y})`);
    planetGroup.innerHTML = `
        <circle r="80" fill="#232f45" filter="url(#glow)" />
        <ellipse rx="32" ry="14" fill="#fff" opacity="0.13" cy="-32" />
    `;
    svg.appendChild(planetGroup);

    // Planetas principales con pulso animado
    mainStars.forEach((star, i) => {
        let angle = star.angle + rotation;
        let dist = 180;
        let sx = center.x + Math.cos(angle) * dist;
        let sy = center.y + Math.sin(angle) * dist;
        let g = document.createElementNS("http://www.w3.org/2000/svg", "g");
        g.setAttribute("class", "planet");
        g.setAttribute("data-name", star.name);
        g.setAttribute("transform", `translate(${sx},${sy})`);
        g.innerHTML = planetSVG(star.color);
        let label = document.createElementNS("http://www.w3.org/2000/svg", "text");
        label.setAttribute("x", 0);
        label.setAttribute("y", 70);
        label.setAttribute("text-anchor", "middle");
        label.setAttribute("fill", "#fff");
        label.setAttribute("font-size", "1.1rem");
        label.setAttribute("font-weight", "bold");
        label.setAttribute("style", "text-shadow:0 2px 8px #1c2c4c;");
        label.textContent = star.name;
        g.appendChild(label);
        svg.appendChild(g);
        star._sx = sx;
        star._sy = sy;
    });

    // Planetas secundarios alrededor del principal seleccionado
    if (zoomed && zoomTarget) {
        const skills = secondaryStars[zoomTarget.name];
        let orbit = document.createElementNS("http://www.w3.org/2000/svg", "circle");
        orbit.setAttribute("cx", zoomTarget._sx);
        orbit.setAttribute("cy", zoomTarget._sy);
        orbit.setAttribute("r", 90);
        orbit.setAttribute("stroke", "#fff");
        orbit.setAttribute("stroke-width", "1.5");
        orbit.setAttribute("fill", "none");
        orbit.setAttribute("opacity", "0.15");
        orbit.setAttribute("stroke-dasharray", "8 8");
        orbit.setAttribute("stroke-dashoffset", frameCount % 16);
        svg.appendChild(orbit);
        
        skills.forEach((skill, i) => {
            let angle = (2 * Math.PI / skills.length) * i + rotation * 1.2;
            let dist = 90;
            let sx = zoomTarget._sx + Math.cos(angle) * dist;
            let sy = zoomTarget._sy + Math.sin(angle) * dist;
            let g = document.createElementNS("http://www.w3.org/2000/svg", "g");
            g.setAttribute("class", "star");
            g.setAttribute("data-name", skill.name);
            // Animación escalonada
            let appearFrame = Math.max(0, frameCount - i * 6);
            let scale = Math.min(1, appearFrame / 12);
            g.setAttribute("transform", `translate(${sx},${sy}) scale(${scale})`);
            g.setAttribute("opacity", scale);
            g.innerHTML = starSVG(skill.color);
            
            if (skill.icon) {
                // Crear el contenedor del icono con fondo circular
                let iconContainer = document.createElementNS("http://www.w3.org/2000/svg", "g");
                iconContainer.setAttribute("class", "icon-container");
                
                // Fondo circular blanco para el icono
                let iconBg = document.createElementNS("http://www.w3.org/2000/svg", "circle");
                iconBg.setAttribute("cx", "0");
                iconBg.setAttribute("cy", "0");
                iconBg.setAttribute("r", "16");
                iconBg.setAttribute("fill", "#fff");
                iconContainer.appendChild(iconBg);
                
                // Crear elemento extranjero para el HTML del icono
                let foreignObject = document.createElementNS("http://www.w3.org/2000/svg", "foreignObject");
                foreignObject.setAttribute("x", "-14");
                foreignObject.setAttribute("y", "-14");
                foreignObject.setAttribute("width", "28");
                foreignObject.setAttribute("height", "28");
                
                // Contenedor HTML para centrar el icono
                let iconDiv = document.createElement("div");
                iconDiv.style.cssText = `
                    width: 28px;
                    height: 28px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    overflow: hidden;
                `;
                
                // Ajustes específicos por icono para centrado perfecto
                let iconAdjustments = {
                    "HTML5": { scale: 0.85, translateX: 0, translateY: 1 },
                    "CSS3": { scale: 0.85, translateX: 0, translateY: 1 },
                    "JavaScript": { scale: 0.85, translateX: 0, translateY: 0 },
                    "React": { scale: 0.8, translateX: 0, translateY: 0 },
                    "Node.js": { scale: 0.85, translateX: 0, translateY: 0 },
                    "MySQL": { scale: 1.1, translateX: 0, translateY: 0 },
                    "Postman": { scale: 0.85, translateX: 0, translateY: 0 },
                    "XAMPP": { scale: 0.85, translateX: 0, translateY: 0 }
                };
                
                let adjustment = iconAdjustments[skill.name] || { scale: 0.85, translateX: 0, translateY: 0 };
                
                // Crear contenedor para el SVG con los ajustes
                let svgContainer = document.createElement("div");
                svgContainer.style.cssText = `
                    transform: scale(${adjustment.scale}) translate(${adjustment.translateX}px, ${adjustment.translateY}px);
                    display: flex;
                    align-items: center;
                    justify-content: center;
                `;
                svgContainer.innerHTML = skill.icon;
                
                iconDiv.appendChild(svgContainer);
                foreignObject.appendChild(iconDiv);
                iconContainer.appendChild(foreignObject);
                
                g.appendChild(iconContainer);
            }
            
            let label = document.createElementNS("http://www.w3.org/2000/svg", "text");
            label.setAttribute("x", 0);
            label.setAttribute("y", 34);
            label.setAttribute("text-anchor", "middle");
            label.setAttribute("fill", "#fff");
            label.setAttribute("font-size", "0.9rem");
            label.setAttribute("font-weight", "bold");
            label.textContent = skill.name;
            g.appendChild(label);
            svg.appendChild(g);
            skill._sx = sx;
            skill._sy = sy;
            skill._r = 18;
        });
    }
}

let frameCount = 0;
function animateGalaxy() {
    frameCount++;
    if (frameCount % 2 === 0) {
        rotation += 0.005;
        drawGalaxy();
    }
    requestAnimationFrame(animateGalaxy);
}
animateGalaxy();

// Interacción: solo un mousemove y un mouseleave
svg.addEventListener('mousemove', function(e) {
    const cursor = getSVGCoords(e);

    // Hover sobre planeta principal
    if (!zoomed) {
        for (const star of mainStars) {
            const dx = cursor.x - star._sx;
            const dy = cursor.y - star._sy;
            if (dx*dx + dy*dy < 60*60) {
                zoomed = true;
                zoomTarget = star;
                hideSkillTooltip();
                drawGalaxy();
                return;
            }
        }
    }

    // Hover sobre planeta secundario
    if (zoomed && zoomTarget) {
        let foundSecondary = false;
        const skills = secondaryStars[zoomTarget.name];
        for (const skill of skills) {
            const dx = cursor.x - skill._sx;
            const dy = cursor.y - skill._sy;
            if (dx*dx + dy*dy < skill._r*skill._r) {
                showSkillTooltip(skill, e);
                foundSecondary = true;
                break;
            }
        }
        if (!foundSecondary) hideSkillTooltip();

        // Si el mouse sale de la zona del planeta principal y secundarias, volver al estado inicial
        let dxMain = cursor.x - zoomTarget._sx;
        let dyMain = cursor.y - zoomTarget._sy;
        if (dxMain*dxMain + dyMain*dyMain > 80*80 && !foundSecondary) {
            zoomed = false;
            zoomTarget = null;
            hideSkillTooltip();
            drawGalaxy();
        }
    }
});

svg.addEventListener('mouseleave', function() {
    zoomed = false;
    zoomTarget = null;
    hideSkillTooltip();
    drawGalaxy();
});

// Tooltip functions
function showSkillTooltip(skill, e) {
    const tooltip = document.getElementById('skill-tooltip');
    tooltip.style.background = "#232f45";
    tooltip.style.color = "#fff";
    tooltip.style.boxShadow = '0 4px 24px #0008';
    tooltip.innerHTML = `
        <div style="display:flex;flex-direction:column;align-items:center;justify-content:center;">
            <div style="width:32px;height:32px;display:flex;align-items:center;justify-content:center;">
                ${skill.icon || ""}
            </div>
            <strong style="margin-top:6px;">${skill.name}</strong>
        </div>
    `;
    tooltip.style.display = 'block';
    const containerRect = svg.parentElement.getBoundingClientRect();
    tooltip.style.left = (e.clientX - containerRect.left + 20) + 'px';
    tooltip.style.top = (e.clientY - containerRect.top - 10) + 'px';
}

function hideSkillTooltip() {
    const tooltip = document.getElementById('skill-tooltip');
    tooltip.style.display = 'none';
}   

//antorcha
    const toggle = document.querySelector(".container input");

    const applyTheme = () => {
      if (toggle.checked) {
        // Antorcha encendida → Light mode
        document.documentElement.setAttribute("data-theme", "light");
      } else {
        // Antorcha apagada → Dark mode
        document.documentElement.setAttribute("data-theme", "dark");
      }
    };

    //generador de estrellas
    applyTheme();
    toggle.addEventListener("change", applyTheme);

    const container = document.querySelector(".floating-elements");
    const numStars = 60;

    for (let i = 0; i < numStars; i++) {
        const star = document.createElement("div");
        star.classList.add("bg-star");

        // posición aleatoria
        star.style.left = Math.random() * 100 + "vw";
        star.style.top = Math.random() * 100 + "vh";

        // tamaño aleatorio
        const size = Math.random() * 3 + 1; 
        star.style.width = size + "px";
        star.style.height = size + "px";

        // opacidad aleatoria para más variedad
        star.style.opacity = Math.random() * 0.7 + 0.1;

        // duración y delay aleatorios
        star.style.animationDuration = (Math.random() * 20 + 8) + "s";
        star.style.animationDelay = Math.random() * 2 + "s";

        container.appendChild(star);
    }

    // Función para actualizar colores de estrellas según el tema
        function updateStarColors() {
        const stars = document.querySelectorAll('.bg-star');
        const isDarkMode = document.body.classList.contains('dark-mode') || 
        document.documentElement.getAttribute('data-theme') === 'dark';
        
        stars.forEach(star => {
            if (isDarkMode) {
                star.style.background = 'white';
                star.style.boxShadow = '0 0 6px rgba(255, 255, 255, 0.8)';
            } else {
                star.style.background = '#333';
                star.style.boxShadow = '0 0 6px rgba(51, 51, 51, 0.8)';
            }
        });
    }

    // Ejecutar al cargar
    updateStarColors();

    // Ejecutar cuando cambie el tema (si tienes un toggle)
    if (typeof toggle !== 'undefined') {
        toggle.addEventListener("change", function() {
            applyTheme();
            setTimeout(updateStarColors, 100); // Pequeño delay para que se aplique el tema
        });
    }